from exhibition_info e, artist_info a, place_info p, thumbnail_file_info t
where rownum <= #{cri.pageNum} * #{cri.amount} and endYn like 'n' and a.artist_no = e.artist_no AND p.place_no = e.place_no AND t.exhibition_no = e.exhibition_no AND p.sido = #{sido} order by e.startDate DESC ) where rn > (#{cri.pageNum} -1) * #{cri.amount}

select * from ( select /*+INDEX_DESC(exhibition_Info pk_exhibition_info)*/ rownum rn, e.exhibition_no, e.title, e.price, e.realm, e.startDate, e.endDate, e.likeCount, e.endYn, a.artist_name, p.place, p.sido, t.fileName, t.filepath, t.uuid, th.theme_no, th.theme_name, et.exhibition_no as themeExhibition_no, NVL((select u.exhibition_no from user_like_exhibition u where u.exhibition_no = e.exhibition_no and u.id = #{id}), 0) as userExhibitionNo, NVL((select u.id from user_like_exhibition u where u.exhibition_no = e.exhibition_no and u.id = #{id}), 'N') as userId, NVL((select u.likeNo from user_like_exhibition u where u.exhibition_no = e.exhibition_no and u.id = #{id}), 0) as userLikeNo
from exhibition_info e, artist_info a, place_info p, thumbnail_file_info t, theme th, exhibition_theme et
where rownum <= #{cri.pageNum} * #{cri.amount} and endYn like 'n' and a.artist_no = e.artist_no AND p.place_no = e.place_no AND t.exhibition_no = e.exhibition_no AND th.theme_no = et.theme_no AND et.exhibition_no = e.exhibition_no AND th.theme_no = #{theme_no} order by e.startDate DESC ) where rn > (#{cri.pageNum} -1) * #{cri.amount}


