from exhibition_info e, artist_info a, place_info p, thumbnail_file_info t, theme th, exhibition_theme et
where rownum <= #{cri.pageNum} * #{cri.amount} and endYn like 'n' and a.artist_no = e.artist_no AND p.place_no = e.place_no AND t.exhibition_no = e.exhibition_no AND th.theme_no = et.theme_no AND et.exhibition_no = e.exhibition_no AND th.theme_name_kor = #{theme_name_kor} order by e.startDate DESC ) where rn > (#{cri.pageNum} -1) * #{cri.amount}

mapper.update:
update exhibition_info
   set likeCount = likeCount + 1
 where exhibition_no = #{exhibition_no}

update exhibition_info
   set likeCount = likeCount - 1
 where exhibition_no = #{exhibition_no}

